import deepCopy from '../../utils/deepCopy';
import { randomizeList, selectOneRandomly } from '../../utils/random';
import { ArtistSFData } from './artistSFData';

const LINES_PER_ROUND = 2;

const blankImage =
  'data:image/png;base64,';

export const createGame = (playerName: string, gameId: string) => (data: ArtistSFData | null) => {
  if (!data) data = {} as ArtistSFData;
  data = deepCopy(data);
  if (!data.gameType) data.gameType = 'artistsf';
  if (data.gameType !== 'artistsf') window.location.href = '/board_games';
  if (!data.id) data.id = gameId;
  if (!data.version) data.version = 0;
  if (!data.players) data.players = [];
  if (!data.stage) data.stage = 'waiting';
  if (!data.playerScore) data.playerScore = {};
  if (!data.roundNumber) data.roundNumber = 0;
  if (!data.words) data.words = [];
  if (!data.randomizedPlayers) data.randomizedPlayers = [];
  if (!data.currentPlayer) data.currentPlayer = 0;
  if (!data.image) data.image = '';
  if (!data.imposter) data.imposter = '';
  if (!data.imposterGuess) data.imposterGuess = {};

  if (data.players.indexOf(playerName) === -1) {
    data.players.push(playerName);
  }
  return data;
};

export const startGame = (data: ArtistSFData) => {
  data = deepCopy(data);
  data.stage = 'enteringWords';
  return data;
};

export const shareMyWord = (player: string, word: string, category: string) => (
  data: ArtistSFData,
) => {
  data = deepCopy(data);
  data.words.push({ player, word, category });
  return data;
};

export const startRound = (data: ArtistSFData) => {
  data = deepCopy(data);

  // first round setup
  if (data.roundNumber === 0) {
    data.words = randomizeList(data.words);
  }

  // add points
  if (data.roundNumber !== 0) {
    let imposterPoints = 0;
    data.players.forEach((player) => {
      data.playerScore[player] = data.playerScore[player] || 0;
      if (player === data.imposter) return;
      if (data.imposterGuess[player] === data.imposter) {
        data.playerScore[player] += 1;
      } else imposterPoints += 1;
    });
    data.playerScore[data.imposter] += imposterPoints;
  }

  // game over
  if (data.roundNumber === data.words.length) {
    data.stage = 'results';
    return data;
  }

  // start next round
  data.randomizedPlayers = randomizeList(data.players);
  data.stage = 'addingLines';
  data.roundNumber++;
  data.image = blankImage;
  data.imposter = selectOneRandomly(
    data.players.filter((p) => p !== data.words[data.roundNumber - 1].player),
  );
  data.currentPlayer = 0;
  data.imposterGuess = {};

  return data;
};

export const shareMyDrawing = (image: string) => (data: ArtistSFData) => {
  data = deepCopy(data);
  data.currentPlayer++;
  data.image = image;
  if (data.currentPlayer >= data.players.length * LINES_PER_ROUND) data.stage = 'guessingImposter';
  return data;
};

export const guessImposter = (player: string, imposter: string) => (data: ArtistSFData) => {
  data = deepCopy(data);
  data.imposterGuess = {
    ...data.imposterGuess,
    [player]: imposter,
  };
  return data;
};

export const revealImposter = (data: ArtistSFData) => {
  data = deepCopy(data);
  data.stage = 'revealImposter';
  return data;
};
