import deepCopy from '../../utils/deepCopy';
import { selectOneRandomly } from '../../utils/random';
import { wordMatch } from '../../utils/wordMatch';
import { StraightLinesData } from './straightLinesData';

export const DRAWINGS_PER_PLAYER = 2;

const blankImage =
  'data:image/png;base64,';

export const createGame = (playerName: string, gameId: string) => (
  data: StraightLinesData | null,
) => {
  if (!data) data = {} as StraightLinesData;
  data = deepCopy(data);
  if (!data.gameType) data.gameType = 'straightlines';
  if (data.gameType !== 'straightlines') window.location.href = '/board_games';
  if (!data.id) data.id = gameId;
  if (!data.version) data.version = 0;
  if (!data.players) data.players = [];
  if (!data.stage) data.stage = 'waiting';
  if (!data.playerScore) data.playerScore = {};
  if (!data.roundNumber) data.roundNumber = 0;
  if (!data.word) data.word = '';
  if (!data.currentPlayer) data.currentPlayer = '';
  if (!data.image) data.image = '';
  if (!data.imageLines) data.imageLines = 0;
  if (!data.playerGuesses) data.playerGuesses = [];

  if (data.players.indexOf(playerName) === -1) {
    data.players.push(playerName);
  }
  return data;
};

export const startRound = (data: StraightLinesData) => {
  data = deepCopy(data);

  // add points
  if (data.currentPlayer) {
    data.players.forEach((player) => {
      data.playerScore[player] = data.playerScore[player] || 0;
      if (player === data.currentPlayer) return;
      if (data.playerGuesses[0].player === player) {
        data.playerScore[player] += 10;
      }
    });
    data.playerScore[data.currentPlayer] += Math.max(0, 20 - data.imageLines);
  }

  // game over
  if (data.roundNumber === data.players.length * DRAWINGS_PER_PLAYER) {
    data.stage = 'results';
    return data;
  }

  // start next round
  data.stage = 'choosingWord';
  data.roundNumber++;
  data.image = blankImage;
  data.imageLines = 0;
  data.playerGuesses = [];
  data.word = '';
  if (!data.currentPlayer) data.currentPlayer = selectOneRandomly(data.players);
  else {
    data.currentPlayer = data.players[data.players.indexOf(data.currentPlayer) + 1];
    if (!data.currentPlayer) data.currentPlayer = data.players[0];
  }

  return data;
};

export const selectWord = (word: string) => (data: StraightLinesData) => {
  data = deepCopy(data);
  data.word = word;
  data.stage = 'drawing';
  return data;
};

export const shareMyDrawing = (image: string) => (data: StraightLinesData) => {
  data = deepCopy(data);
  data.image = image;
  data.imageLines++;
  return data;
};

export const guessWord = (player: string, word: string) => (data: StraightLinesData) => {
  if (data.stage !== 'drawing') return data;
  data = deepCopy(data);
  data.playerGuesses = [{ player, word }, ...data.playerGuesses];
  if (wordMatch(word, data.word)) {
    data.stage = 'roundPoints';
  }
  return data;
};
